import style from "../ContactForm/ContactForm.module.css";
import React, { useState, useContext } from "react";
import Emailcontext from "../../context/Email/Emaitcontext";

const ContactForm = () => {
  const CLIENT_ID =
    "486661095255-m3ppko6s3bnaechni1uvmc60ovroheac.apps.googleusercontent.com";
  const CLEINT_SECRET = "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";
  const REDIRECT_URI = "https://developers.google.com/oauthplayground";
  const REFRESH_TOKEN =
    "1//042xVjKvotZiLCgYIARAAGAQSNwF-L9IrGYAxXh07QkbA2Ig27UKF8HVkC7Enadko2wXBbNVa4xaFWv2Cw8RoEZ7oBLBnvoAtTeE";
  const oAuth2Client = new google.auth.OAuth2(
    CLIENT_ID,
    CLEINT_SECRET,
    REDIRECT_URI
  );
  const a = useContext(Emailcontext);
  const [FormData, setFormData] = useState({
    name: "",
    email: "",
    msg: "",
  });
  const handleonchange = (event, field) => {
    setFormData({
      ...FormData,
      [field]: event.target.value,
    });
  };
  async function sendMail(Data) {
    try {
      const accessToken = await oAuth2Client.getAccessToken();

      const transport = nodemailer.createTransport({
        service: "gmail",
        auth: {
          type: "OAuth2",
          user: "easycoding2000@gmail.com",
          clientId: CLIENT_ID,
          clientSecret: CLEINT_SECRET,
          refreshToken: REFRESH_TOKEN,
          accessToken: accessToken,
        },
      });

      const mailOptions = {
        from: "easyCoding <easycoding2000@gmail.com>",
        to: ["bhomickyadav786@gmail.com", "mahakagarwal248@gmail.com"],
        subject: "Hello from gmail using API",
        text: `username:${Data.name}\n userid:${Data.email}\n message:${Data.msg} `,
        // html: "<h1>Hello from gmail email using API</h1>",
      };

      const result = await transport.sendMail(mailOptions);
      return result;
    } catch (error) {
      return error;
    }
  }
  return (
    <div className={style.firstdiv}>
      <form>
        <label htmlFor="name">full name:</label>
        <br />
        <input
          type="text"
          value={FormData.name}
          onChange={(event) => handleonchange(event, "name")}
          id="name"
          name="fname"
        />
        <br />
        <label htmlFor="email">Your email</label>
        <br />
        <input
          type="email"
          onChange={(event) => handleonchange(event, "email")}
          placeholder="example@gmai.com"
          value={FormData.email}
          id="email"
          name="lname"
        />
        <br />

        <label htmlFor="textarea">your message:</label>
        <br />
        <textarea
          className={style.mytextarea}
          onChange={(event) => handleonchange(event, "msg")}
          cols="2"
          id="textarea"
          rows="3"
          value={FormData.msg}
        ></textarea>
      </form>
      <button
        onClick={() => {
          onsubmit();
        }}
      >
        send
      </button>
    </div>
  );
};

export default ContactForm;
